 \draw[black, thick] (\x,0.14) --++(0,-.28) node[below, yshift=-2, fill=white, inner sep=1] {$\x$};
 \draw[black, thick] (0.14,\x) --++(-.28,0) node[left, xshift=-2, fill=white, inner sep=1] {$\x$};
 };
\draw[red!80!black,ultra thick] (0,0) ellipse (4 and 2);
\end{tikzpicture}


