\tikzset{%
}
\begin{tikzpicture} [scale=.3]
\pgfkeys{/pgf/declare function={asinh(\x) = ln(\x + sqrt(\x^2+1));}}\draw[cyan] (-10,-10) grid (10,10);
\draw[black,thick,->,>=stealth'] (-10,0)--(10.8,0) node[right] {$x$};
\draw[black,thick,->,>=stealth'] (0,-10)--(0,10.8) node[above] {$y$};
\def\a{sqrt(24)};
\def\b{sqrt(12)};
\draw[samples=65,domain=-10:10,smooth,variable=\x,black,dashed] plot ({\x},{\b / \a * (\x))});
\draw[samples=65,domain=-10:10,smooth,variable=\x,black,dashed] plot ({\x},{-\b / \a * (\x))});

\foreach \x   in  {-5,5,-10,10}
{ \draw[black, thick] (\x,0.14) --++(0,-.28) node[below, yshift=-2, fill=white, inner sep=1, scale=.9] {$\x$};
 \draw[black, thick] (0.14,\x) --++(-.28,0) node[left, xshift=-2, fill=white, inner sep=1, scale=.9] {$\x$};}

\draw [domain=-asinh(10 / \a):asinh(10 / \a),variable=\t, smooth, samples=75, red, thick] plot ({\a*sinh(\t) }, {\b* cosh(\t) } );
\draw [domain=-asinh(10 / \a):asinh(10 / \a),variable=\t, smooth, samples=75, red, thick] plot ({\a*sinh(\t) }, {-\b* cosh(\t) } );        \end{tikzpicture}
